mapper.select:
select 

from t_problem_statistic


        and epic_key = #{epicKey,jdbcType=VARCHAR}


        and req_no = #{reqNo,jdbcType=VARCHAR}


        and requirements_review_number = #{requirementsReviewNumber,jdbcType=VARCHAR}


        and code_review_number = #{codeReviewNumber,jdbcType=VARCHAR}


        and test_review_number = #{testReviewNumber,jdbcType=VARCHAR}


        and production_review_number = #{productionReviewNumber,jdbcType=VARCHAR}


        and technical_review_number = #{technicalReviewNumber,jdbcType=VARCHAR}


        and other_reviews_number = #{otherReviewsNumber,jdbcType=VARCHAR}


        and external_defects_number = #{externalDefectsNumber,jdbcType=VARCHAR}


        and version_defects_number = #{versionDefectsNumber,jdbcType=VARCHAR}


        and parameter_defects_number = #{parameterDefectsNumber,jdbcType=VARCHAR}


        and function_defects_number = #{functionDefectsNumber,jdbcType=VARCHAR}


        and process_defects_number = #{processDefectsNumber,jdbcType=VARCHAR}


        and prompt_defects_number = #{promptDefectsNumber,jdbcType=VARCHAR}


        and page_defects_number = #{pageDefectsNumber,jdbcType=VARCHAR}


        and background_defects_number = #{backgroundDefectsNumber,jdbcType=VARCHAR}


        and modify_defects_number = #{modifyDefectsNumber,jdbcType=VARCHAR}


        and design_defects_number = #{designDefectsNumber,jdbcType=VARCHAR}


        and invalid_defects_number = #{invalidDefectsNumber,jdbcType=VARCHAR}
