mapper.select:
select

from t_working_hours


        and jiraWorklogKey = #{jiraworklogkey,jdbcType=VARCHAR}


        and issuekey = #{issuekey,jdbcType=VARCHAR}


        and timeSpnet = #{timespnet,jdbcType=VARCHAR}


        and subtaskName = #{subtaskname,jdbcType=VARCHAR}


        and assignment_department = #{assignmentDepartment,jdbcType=VARCHAR}


        and name = #{name,jdbcType=VARCHAR}


        and displayName = #{displayname,jdbcType=VARCHAR}


        and devp_lead_dept = #{devpLeadDept,jdbcType=VARCHAR}


        and comment = #{comment,jdbcType=VARCHAR}


        and createdTime = #{createdtime,jdbcType=VARCHAR}


        and startedTime = #{startedtime,jdbcType=VARCHAR}


        and updatedTime = #{updatedtime,jdbcType=VARCHAR}

select a.fullname displayName, IFNULL(sum(b.timeSpnet),0) AS sumTime, department devp_lead_dept from sys_user a left join t_working_hours b on a.fullname=b.displayName and LEFT(b.startedTime,10)= #{selectTime,jdbcType=VARCHAR}
and department = #{devpLeadDept,jdbcType=VARCHAR}
group by a.fullname

select a.fullname displayName, IFNULL(sum(b.timeSpnet),0) AS sumTime, department devp_lead_dept from sys_user a left join t_working_hours b on a.fullname=b.displayName and YEARWEEK(date_format(b.startedTime,'%Y-%m-%d')) = YEARWEEK(#{selectTime,jdbcType=VARCHAR})
and department = #{devpLeadDept,jdbcType=VARCHAR}
group by a.fullname

select a.fullname displayName, IFNULL(sum(b.timeSpnet),0) AS sumTime, department devp_lead_dept from sys_user a left join t_working_hours b on a.fullname=b.displayName and LEFT(b.startedTime,10) like CONCAT('%',#{selectTime},'%')
and department = #{devpLeadDept,jdbcType=VARCHAR}
group by a.fullname

select sum(1) sumDept ,sum(c.sumTime) sumTime from ( select a.fullname displayName, IFNULL(sum(b.timeSpnet),0) AS sumTime, department devp_lead_dept from sys_user a left join t_working_hours b on a.fullname=b.displayName and LEFT(b.startedTime,10)= #{selectTime,jdbcType=VARCHAR}
and department = #{devpLeadDept,jdbcType=VARCHAR}
group by a.fullname ) c

select sum(1) sumDept ,sum(c.sumTime) sumTime from ( select a.fullname displayName, IFNULL(sum(b.timeSpnet),0) AS sumTime, department devp_lead_dept from sys_user a left join t_working_hours b on a.fullname=b.displayName and YEARWEEK(date_format(b.startedTime,'%Y-%m-%d')) = YEARWEEK(#{selectTime,jdbcType=VARCHAR})
and department = #{devpLeadDept,jdbcType=VARCHAR}
group by a.fullname ) c

select sum(1) sumDept ,sum(c.sumTime) sumTime from ( select a.fullname displayName, IFNULL(sum(b.timeSpnet),0) AS sumTime, department devp_lead_dept from sys_user a left join t_working_hours b on a.fullname=b.displayName and LEFT(b.startedTime,10) like CONCAT('%',#{selectTime},'%')
and department = #{devpLeadDept,jdbcType=VARCHAR}
group by a.fullname ) c

select
sum(timeSpnet) timeSpnet ,

from
(select

from t_working_hours


        and displayName = #{displayname,jdbcType=VARCHAR}


        and LEFT(startedTime,10) = #{selectTime,jdbcType=VARCHAR}

 )c GROUP BY c.issuekey

