from ( select
from t_working_hours
and displayName = #{displayname,jdbcType=VARCHAR}
and YEARWEEK(date_format(startedTime,'%Y-%m-%d')) = YEARWEEK(#{selectTime,jdbcType=VARCHAR})
)c GROUP BY c.issuekey

